mapper.update:
UPDATE fa_member m


        SET m.balance = m.balance + #{money}


        SET m.balance = m.balance - #{money}




        SET m.balance_spot = m.balance_spot + #{money}


        SET m.balance_spot = m.balance_spot - #{money}




        SET m.balance_contract = m.balance_contract + #{money}


        SET m.balance_contract = m.balance_contract - #{money}




        SET m.balance_financing = m.balance_financing + #{money}


        SET m.balance_financing = m.balance_financing - #{money}


WHERE m.id = #{userId}

UPDATE fa_member m


        SET m.freeze_profit = m.freeze_profit + #{money}


        SET m.freeze_profit = IF(m.freeze_profit < #{money}, 0, m.freeze_profit - #{money})




        SET m.freeze_profit_spot = m.freeze_profit_spot + #{money}


        SET m.freeze_profit_spot = IF(m.freeze_profit_spot < #{money}, 0, m.freeze_profit_spot - #{money})




        SET m.freeze_profit_contract = m.freeze_profit_contract + #{money}


        SET m.freeze_profit_contract = IF(m.freeze_profit_contract < #{money}, 0, m.freeze_profit_contract - #{money})




        SET m.freeze_profit_financing = m.freeze_profit_financing + #{money}


        SET m.freeze_profit_financing = IF(m.freeze_profit_financing < #{money}, 0, m.freeze_profit_financing - #{money})


WHERE m.id = #{userId}
