mapper.select:


     and r.id = #{id}
     and r.user_id = #{userId}
     and r.mobile = #{mobile}
     and r.name like concat('%', #{name}, '%')
     and r.superior_id = #{superiorId}
     and r.superior_code = #{superiorCode}
     and r.superior_name like concat('%', #{superiorName}, '%')
     and r.money = #{money}
     and r.create_time = #{createTime}
     and r.update_time = #{updateTime}
     and r.delete_time = #{deleteTime}
     and r.pay_time = #{payTime}
     and r.pay_type = #{payType}
     and r.is_pay = #{isPay}
     and r.is_remit = #{isRemit}
     and r.reject = #{reject}
     and r.is_simulation = #{isSimulation}
     and r.sysbankid = #{sysbankid}
     and r.is_qx = #{isQx}
     and r.delete_flag = #{deleteFlag}
     and r.order_id = #{orderId}
     and r.transaction_id = #{transactionId}


        AND date_format(r.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(r.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

     and m.weiyima like concat('%', #{weiyima}, '%')

and r.user_id in (
    SELECT
        m.id
    FROM
        fa_member m
    WHERE
        m.daili_id IN ( SELECT #{parentId} UNION ALL SELECT u.user_id FROM sys_user u WHERE u.path LIKE concat('%,', #{parentId}, ',%') )
)
order by r.id desc

mapper.update:
update fa_recharge
user_id = #{userId}, mobile = #{mobile}, name = #{name}, superior_id = #{superiorId}, superior_code = #{superiorCode}, superior_name = #{superiorName}, money = #{money}, create_time = #{createTime}, update_time = #{updateTime}, delete_time = #{deleteTime}, pay_time = #{payTime}, pay_type = #{payType}, is_pay = #{isPay}, is_remit = #{isRemit}, reject = #{reject}, is_simulation = #{isSimulation}, sysbankid = #{sysbankid}, is_qx = #{isQx}, delete_flag = #{deleteFlag}, order_id = #{orderId}, transaction_id = #{transactionId}, is_approve = #{isApprove}, remarks = #{remarks},
where id = #{id}

mapper.select:
select COALESCE(SUM(r.money), 0) total from fa_recharge r

     and r.id = #{id}
     and r.user_id = #{userId}
     and r.mobile = #{mobile}
     and r.name like concat('%', #{name}, '%')
     and r.superior_id = #{superiorId}
     and r.superior_code = #{superiorCode}
     and r.superior_name like concat('%', #{superiorName}, '%')
     and r.money = #{money}
     and r.create_time = #{createTime}
     and r.update_time = #{updateTime}
     and r.delete_time = #{deleteTime}
     and r.pay_time = #{payTime}
     and r.pay_type = #{payType}
     and r.is_pay = #{isPay}
     and r.is_remit = #{isRemit}
     and r.reject = #{reject}
     and r.is_simulation = #{isSimulation}
     and r.sysbankid = #{sysbankid}
     and r.delete_flag = #{deleteFlag}
     and m.daili_id = #{dailiId}

UNION ALL
select COALESCE(SUM(r.money), 0) toPay from fa_recharge r

    and r.is_pay = 0
     and r.id = #{id}
     and r.user_id = #{userId}
     and r.mobile = #{mobile}
     and r.name like concat('%', #{name}, '%')
     and r.superior_id = #{superiorId}
     and r.superior_code = #{superiorCode}
     and r.superior_name like concat('%', #{superiorName}, '%')
     and r.money = #{money}
     and r.create_time = #{createTime}
     and r.update_time = #{updateTime}
     and r.delete_time = #{deleteTime}
     and r.pay_time = #{payTime}
     and r.pay_type = #{payType}
     and r.is_pay = #{isPay}
     and r.is_remit = #{isRemit}
     and r.reject = #{reject}
     and r.is_simulation = #{isSimulation}
     and r.sysbankid = #{sysbankid}
     and r.delete_flag = #{deleteFlag}
     and m.daili_id = #{dailiId}

UNION ALL
select COALESCE(SUM(r.money), 0) paid from fa_recharge r

    and r.is_pay = 1
     and r.id = #{id}
     and r.user_id = #{userId}
     and r.mobile = #{mobile}
     and r.name like concat('%', #{name}, '%')
     and r.superior_id = #{superiorId}
     and r.superior_code = #{superiorCode}
     and r.superior_name like concat('%', #{superiorName}, '%')
     and r.money = #{money}
     and r.create_time = #{createTime}
     and r.update_time = #{updateTime}
     and r.delete_time = #{deleteTime}
     and r.pay_time = #{payTime}
     and r.pay_type = #{payType}
     and r.is_pay = #{isPay}
     and r.is_remit = #{isRemit}
     and r.reject = #{reject}
     and r.is_simulation = #{isSimulation}
     and r.sysbankid = #{sysbankid}
     and r.delete_flag = #{deleteFlag}
     and m.daili_id = #{dailiId}

UNION ALL
select COALESCE(SUM(r.money), 0) reject from fa_recharge r

    and r.is_pay = 2
     and r.id = #{id}
     and r.user_id = #{userId}
     and r.mobile = #{mobile}
     and r.name like concat('%', #{name}, '%')
     and r.superior_id = #{superiorId}
     and r.superior_code = #{superiorCode}
     and r.superior_name like concat('%', #{superiorName}, '%')
     and r.money = #{money}
     and r.create_time = #{createTime}
     and r.update_time = #{updateTime}
     and r.delete_time = #{deleteTime}
     and r.pay_time = #{payTime}
     and r.pay_type = #{payType}
     and r.is_pay = #{isPay}
     and r.is_remit = #{isRemit}
     and r.reject = #{reject}
     and r.is_simulation = #{isSimulation}
     and r.sysbankid = #{sysbankid}
     and r.delete_flag = #{deleteFlag}
     and m.daili_id = #{dailiId}

select COALESCE(SUM(r.money), 0) total from fa_recharge r
left join fa_member m
on r.user_id = m.id

     and r.id = #{id}
     and r.user_id = #{userId}
     and r.mobile = #{mobile}
     and r.name like concat('%', #{name}, '%')
     and r.superior_id = #{superiorId}
     and r.superior_code = #{superiorCode}
     and r.superior_name like concat('%', #{superiorName}, '%')
     and r.money = #{money}
     and r.create_time = #{createTime}
     and r.update_time = #{updateTime}
     and r.delete_time = #{deleteTime}
     and r.pay_time = #{payTime}
     and r.pay_type = #{payType}
     and r.is_pay = #{isPay}
     and r.is_remit = #{isRemit}
     and r.reject = #{reject}
     and r.is_simulation = #{isSimulation}
     and r.sysbankid = #{sysbankid}
     and r.delete_flag = #{deleteFlag}


        AND date_format(r.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(r.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

    and r.is_pay = 1 and r.delete_flag = 0

and m.daili_id in ( select #{parentId} union all select u.user_id from sys_user u where u.path like concat('%,', #{parentId}, ',%'))

