mapper.select:


     and sg.id = #{id}
     and sg.user_id = #{userId}
     and sg.shengou_id = #{shengouId}
     and sg.code = #{code}
     and sg.all_code = #{allCode}
     and sg.name like concat('%', #{name}, '%')
     and sg.sg_fx_price = #{sgFxPrice}
     and sg.sg_hy_rate = #{sgHyRate}
     and sg.sg_num = #{sgNum}
     and sg.sg_nums = #{sgNums}
     and sg.max_sg = #{maxSg}
     and sg.status = #{status}
     and sg.money = #{money}
     and sg.create_time = #{createTime}
     and sg.update_time = #{updateTime}
     and sg.zq_num = #{zqNum}
     and sg.dj_money = #{djMoney}
     and sg.sg_sg_date = #{sgSgDate}
     and sg.sg_zq_jk_date = #{sgZqJkDate}
     and sg.sg_ss_date = #{sgSsDate}
     and sg.is_cc_time = #{isCcTime}
     and sg.is_cc = #{isCc}
     and sg.zq_money = #{zqMoney}
     and sg.is_tz = #{isTz}
     and sg.is_tz_time = #{isTzTime}
     and sg.zq_nums = #{zqNums}
     and sg.delete_flag = #{deleteFlag}
     and sg.sg_type = #{sgType}
     and sg.renjiao = #{renjiao}
     and sg.renjiao_time = #{renjiaoTime}
     and sg.pay_later = #{payLater}

     and m.name like concat('%', #{memberName}, '%')
     and m.mobile like concat('%', #{mobile}, '%')

and sg.user_id in (
    SELECT
        m.id
    FROM
        fa_member m
    WHERE
        m.daili_id IN ( SELECT #{parentId} UNION ALL SELECT u.user_id FROM sys_user u WHERE u.path LIKE concat('%,', #{parentId}, ',%') )
)
order by sg.id desc

mapper.update:
update fa_stock_ps
user_id = #{userId}, shengou_id = #{shengouId}, code = #{code}, all_code = #{allCode}, name = #{name}, sg_fx_price = #{sgFxPrice}, sg_hy_rate = #{sgHyRate}, sg_num = #{sgNum}, sg_nums = #{sgNums}, max_sg = #{maxSg}, status = #{status}, money = #{money}, create_time = #{createTime}, update_time = #{updateTime}, zq_num = #{zqNum}, dj_money = #{djMoney}, sg_sg_date = #{sgSgDate}, sg_zq_jk_date = #{sgZqJkDate}, sg_ss_date = #{sgSsDate}, is_cc_time = #{isCcTime}, is_cc = #{isCc}, zq_money = #{zqMoney}, is_tz = #{isTz}, is_tz_time = #{isTzTime}, zq_nums = #{zqNums}, delete_flag = #{deleteFlag}, sg_type = #{sgType}, renjiao = #{renjiao}, renjiao_time = #{renjiaoTime}, pay_later = #{payLater},
where id = #{id}
