mapper.select:


     and shd.id = #{id}
     and shd.hold_id = #{holdId}
     and shd.member_id = #{memberId}
     and shd.pid = #{pid}
     and shd.pid_code = #{pidCode}
     and shd.pid_name like concat('%', #{pidName}, '%')
     and shd.stock_id = #{stockId}
     and shd.stock_name like concat('%', #{stockName}, '%')
     and shd.stock_symbol = #{stockSymbol}
     and shd.all_code = #{allCode}
     and shd.stock_type = #{stockType}
     and shd.hold_hand = #{holdHand}
     and shd.hold_number = #{holdNumber}
     and shd.hold_type = #{holdType}
     and shd.average = #{average}
     and shd.profit_lose = #{profitLose}
     and shd.resource_type = #{resourceType}
     and shd.create_time = #{createTime}
     and shd.update_time = #{updateTime}
     and shd.status = #{status}
     and shd.delete_flag = #{deleteFlag}
     and shd.remark = #{remark}
     and shd.freeze_number = #{freezeNumber}
     and shd.freeze_days_left = #{freezeDaysLeft}
     and shd.freeze_status = #{freezeStatus}
     and shd.new_stock_id = #{newStockId}
     and shd.is_list = #{isList}
     and shd.is_lock = #{isLock}
     and shd.buy_price = #{buyPrice}
     and shd.buy_poundage = #{buyPoundage}

        AND date_format(shd.buy_time,'%y%m%d') >= date_format(#{params.beginBuyTime},'%y%m%d')


        AND date_format(shd.buy_time,'%y%m%d') <= date_format(#{params.endBuyTime},'%y%m%d')

     and shd.sell_price = #{sellPrice}
     and shd.sell_poundage = #{sellPoundage}
     and shd.sell_stamp_duty = #{sellStampDuty}

        AND date_format(shd.sell_time,'%y%m%d') >= date_format(#{params.beginSellTime},'%y%m%d')


        AND date_format(shd.sell_time,'%y%m%d') <= date_format(#{params.endSellTime},'%y%m%d')

     and shd.trading_hand = #{tradingHand}
     and shd.trading_number = #{tradingNumber}

     and m.name like concat('%', #{memberName}, '%')
     and m.mobile like concat('%', #{mobile}, '%')

and shd.member_id in (
    SELECT
        m.id
    FROM
        fa_member m
    WHERE
        m.daili_id IN ( SELECT #{parentId} UNION ALL SELECT u.user_id FROM sys_user u WHERE u.path LIKE concat('%,', #{parentId}, ',%') )
)
order by shd.id desc

mapper.update:
update fa_stock_hold_detail
hold_id = #{holdId}, member_id = #{memberId}, pid = #{pid}, pid_code = #{pidCode}, pid_name = #{pidName}, stock_id = #{stockId}, stock_name = #{stockName}, stock_symbol = #{stockSymbol}, all_code = #{allCode}, stock_type = #{stockType}, hold_hand = #{holdHand}, hold_number = #{holdNumber}, hold_type = #{holdType}, average = #{average}, profit_lose = #{profitLose}, resource_type = #{resourceType}, create_time = #{createTime}, update_time = #{updateTime}, status = #{status}, delete_flag = #{deleteFlag}, remark = #{remark}, freeze_number = #{freezeNumber}, freeze_days_left = #{freezeDaysLeft}, freeze_status = #{freezeStatus}, new_stock_id = #{newStockId}, is_list = #{isList}, is_lock = #{isLock}, buy_price = #{buyPrice}, buy_poundage = #{buyPoundage}, buy_time = #{buyTime}, sell_price = #{sellPrice}, sell_poundage = #{sellPoundage}, sell_stamp_duty = #{sellStampDuty}, sell_time = #{sellTime}, trading_hand = #{tradingHand}, trading_number = #{tradingNumber},
where id = #{id}
