mapper.select:


     and st.id = #{id}
     and st.member_id = #{memberId}
     and st.stock_id = #{stockId}
     and st.stock_name like concat('%', #{stockName}, '%')
     and st.stock_symbol = #{stockSymbol}
     and st.all_code = #{allCode}
     and st.hold_id = #{holdId}
     and st.date = #{date}
     and st.time = #{time}
     and st.trading_hand = #{tradingHand}
     and st.trading_number = #{tradingNumber}
     and st.trading_price = #{tradingPrice}
     and st.trading_amount = #{tradingAmount}
     and st.trading_type = #{tradingType}
     and st.trading_poundage = #{tradingPoundage}
     and st.stamp_duty = #{stampDuty}
     and st.status = #{status}
     and st.create_time = #{createTime}
     and st.update_time = #{updateTime}
     and st.delete_flag = #{deleteFlag}
     and st.remark = #{remark}

     and m.name like concat('%', #{memberName}, '%')
     and m.mobile like concat('%', #{mobile}, '%')

and st.member_id in (
SELECT
m.id
FROM
fa_member m
WHERE
m.daili_id IN ( SELECT #{parentId} UNION ALL SELECT u.user_id FROM sys_user u WHERE u.path LIKE concat('%,', #{parentId}, ',%') )
)
order by st.id desc

mapper.update:
update fa_stock_trading
member_id = #{memberId}, stock_id = #{stockId}, stock_name = #{stockName}, stock_symbol = #{stockSymbol}, all_code = #{allCode}, hold_id = #{holdId}, date = #{date}, time = #{time}, trading_hand = #{tradingHand}, trading_number = #{tradingNumber}, trading_price = #{tradingPrice}, trading_amount = #{tradingAmount}, trading_type = #{tradingType}, trading_poundage = #{tradingPoundage}, stamp_duty = #{stampDuty}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, delete_flag = #{deleteFlag}, remark = #{remark},
where id = #{id}

mapper.select:
select
    COALESCE(sum( st.trading_poundage ), 0) buyPoundage,
    COALESCE(sum( st.stamp_duty ), 0) stampDuty
from fa_stock_trading st
    left join fa_member m
    on st.member_id = m.id

     and st.id = #{id}
     and st.member_id = #{memberId}
     and st.stock_id = #{stockId}
     and st.stock_name like concat('%', #{stockName}, '%')
     and st.stock_symbol = #{stockSymbol}
     and st.all_code = #{allCode}
     and st.hold_id = #{holdId}
     and st.date = #{date}
     and st.time = #{time}
     and st.trading_number = #{tradingNumber}
     and st.trading_price = #{tradingPrice}
     and st.trading_amount = #{tradingAmount}
     and st.trading_type = #{tradingType}
     and st.trading_poundage = #{tradingPoundage}
     and st.stamp_duty = #{stampDuty}
     and st.status = #{status}
     and st.create_time = #{createTime}
     and st.update_time = #{updateTime}
     and st.delete_flag = #{deleteFlag}
     and st.remark = #{remark}
     and m.daili_id = #{dailiId}
     and m.name like concat('%', #{memberName}, '%')
     and m.mobile like concat('%', #{mobile}, '%')
     and m.is_simulation = #{isSimulation}

select
COALESCE(sum( st.trading_poundage ), 0) buyPoundage
from fa_stock_trading st
left join fa_member m
on st.member_id = m.id

     and st.id = #{id}
     and st.member_id = #{memberId}
     and st.stock_id = #{stockId}
     and st.stock_name like concat('%', #{stockName}, '%')
     and st.stock_symbol = #{stockSymbol}
     and st.all_code = #{allCode}
     and st.hold_id = #{holdId}
     and st.date = #{date}
     and st.time = #{time}
     and st.trading_number = #{tradingNumber}
     and st.trading_price = #{tradingPrice}
     and st.trading_amount = #{tradingAmount}
     and st.trading_type = #{tradingType}
     and st.trading_poundage = #{tradingPoundage}
     and st.stamp_duty = #{stampDuty}
     and st.status = #{status}
     and st.create_time = #{createTime}
     and st.update_time = #{updateTime}
     and st.delete_flag = #{deleteFlag}
     and st.remark = #{remark}

     and m.name like concat('%', #{memberName}, '%')
     and m.mobile like concat('%', #{mobile}, '%')
     and m.is_simulation = #{isSimulation}

and st.trading_type = 1
and m.daili_id in ( select #{parentId} union all select u.user_id from sys_user u where u.path like concat('%,', #{parentId}, ',%'))

select
COALESCE(sum( st.trading_poundage ), 0) sellPoundage
